// Algorithm to convert a negative number to its absolute value

// Step 1: 
// Create a variable `absVal` and set it equal to the negative number

let absVal = -5

// Step 2: 
// Check if the number is negative 

if (absVal < 0) {

  // Step 3: 
  // If the number is negative, multiply it by -1 to get its absolute value
  absVal = absVal * -1

}

// Step 4: 
// Return the absolute value of the number

return absVal;
